mapper.select:
SELECT crl.*, u.true_name, c.customer_name FROM `t_customer_return_list` crl JOIN t_user u USING (user_id) JOIN t_customer c USING (customer_id)
DATE_FORMAT(crl.return_date, '%Y-%m-%d') >= DATE_FORMAT(#{sTime}, '%Y-%m-%d') AND DATE_FORMAT(crl.return_date, '%Y-%m-%d') <= DATE_FORMAT(#{eTime}, '%Y-%m-%d')
AND crl.return_number LIKE CONCAT('%', #{returnNumber}, '%')
AND sl.customer_id = #{customerId}
AND pl.state = #{state}
