mapper.select:
SELECT pl.*, u.true_name, s.supplier_name FROM `t_purchase_list` pl JOIN t_user u USING (user_id) JOIN t_supplier s USING (supplier_id)
DATE_FORMAT(pl.purchase_date, '%Y-%m-%d') >= DATE_FORMAT(#{sTime}, '%Y-%m-%d') AND DATE_FORMAT(pl.purchase_date, '%Y-%m-%d') <= DATE_FORMAT(#{eTime}, '%Y-%m-%d')
AND pl.purchase_number LIKE CONCAT('%', #{purchaseNumber}, '%')
AND pl.supplier_id = #{supplierId}
AND pl.state = #{state}
