mapper.select:
SELECT rl.*, u.true_name, s.supplier_name FROM `t_return_list` rl JOIN t_user u USING (user_id) JOIN t_supplier s USING (supplier_id)
DATE_FORMAT(rl.return_date, '%Y-%m-%d') >= DATE_FORMAT(#{sTime}, '%Y-%m-%d') AND DATE_FORMAT(rl.return_date, '%Y-%m-%d') <= DATE_FORMAT(#{eTime}, '%Y-%m-%d')
AND rl.return_number LIKE CONCAT('%', #{returnNumber}, '%')
AND rl.supplier_id = #{supplierId}
AND rl.state = #{state}
